var addItems=document.querySelector(".add-items"),itemsList=document.querySelector(".plates"),items=JSON.parse(localStorage.getItem("items"))||[];function addItem(a){a.preventDefault();a={text:this.querySelector("[name=item]").value,done:!1};items.push(a);populateList(items,itemsList);localStorage.setItem("items",JSON.stringify(items));this.reset()}
function populateList(a,c){a=void 0===a?[]:a;c.innerHTML=a.map(function(a,b){return'\n        <li>\n            <input type="checkbox" data-index='+b+' id="item'+b+'" '+(a.done?"checked":"")+'>\n            <label for="item'+b+'">'+a.text+"</label>\n        </li>\n        "}).join("")}function toggleDone(a){a.target.matches("input")&&(a=a.target.dataset.index,items[a].done=!items[a].done,localStorage.setItem("items",JSON.stringify(items)))}addItems.addEventListener("submit",addItem);
itemsList.addEventListener("click",toggleDone);populateList(items,itemsList);
