var bandsList="The Plot in You;The Devil Wears Prada;Pierce the Veil;Norma Jean;The Bled;Say Anything;The Midway State;We Came as Romans;Counterparts;Oh, Sleeper;A Skylit Drive;Anywhere But Here;An Old Dog".split(";"),regex=/^(a |the |an )/i,strip=function(a){return a.replace(regex,"").trim()},sortedBands=bandsList.sort(function(a,b){return strip(a)>strip(b)?1:-1});document.getElementById("bands").innerHTML=sortedBands.map(function(a){return"<li>"+a+"</li>"}).join("");
